,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'WITH C AS ( SELECT',
'                LEVEL   AS COLUMN_ID,',
'                ''Column '' || LEVEL AS COLUMN_TITLE,',
'                CASE',
'                    WHEN MOD(ROWNUM, 3) = 0 THEN',
'                        ''fa-emoji-cool''',
'                    WHEN MOD(ROWNUM, 3) = 1 THEN',
'                        ''fa-emoji-scream''',
'                    WHEN MOD(ROWNUM, 3) = 2 THEN',
'                        ''fa-emoji-sleeping''',
'                END AS COLUMN_ICON,',
'                ''hsl('' || TRUNC(DBMS_RANDOM.VALUE(0, 360)) || '', 55%, 60%)'' AS COLUMN_COLOR,',
'                NULL AS COLUMN_HEADER_STYLE',
'            FROM',
'                DUAL',
'            CONNECT BY',
'                LEVEL <= DBMS_RANDOM.VALUE(2, 8)',
'), G AS ( SELECT',
'              LEVEL   AS GROUP_ID,',
'              ''Group '' || LEVEL AS GROUP_TITLE,',
'              ''fa-cogs'' AS GROUP_ICON,',
'              ''<i class="fa fa-pie-chart-'' || TRUNC(DBMS_RANDOM.VALUE(0, 21)) * 5 || ''" style="font-size: 3.5em;margin-left: 5px;">'' AS GROUP_FOOTER',
'          FROM',
'              DUAL',
'          CONNECT BY',
'              LEVEL <= 3',
'), I AS ( SELECT',
'              1000 + LEVEL AS ID,',
'              TRUNC(DBMS_RANDOM.VALUE(1,(SELECT',
'                                             MAX(COLUMN_ID) + 1',
'                                         FROM',
'                                             C',
'                                        ))) AS COLUMN_ID,',
'              TRUNC(DBMS_RANDOM.VALUE(1,(SELECT',
'                                             MAX(GROUP_ID) + 1',
'                                         FROM',
'                                             G',
'                                        ))) AS GROUP_ID,',
'              ''Item '' || LEVEL AS TITLE,',
'              ''<i class="fa fa-pie-chart-'' || TRUNC(DBMS_RANDOM.VALUE(0, 21)) * 5 || ''" style="font-size: 3.5em;margin-left: 5px;">'' AS FOOTER',
'          FROM',
'              DUAL',
'          CONNECT BY',
'              LEVEL <= TRUNC(DBMS_RANDOM.VALUE(10, 20))',
')',
'SELECT',
'    COLUMN_ID,',
'    COLUMN_TITLE,',
'    COLUMN_ICON,',
'    COLUMN_COLOR,',
'    COLUMN_HEADER_STYLE,',
'    GROUP_ID,',
'    GROUP_TITLE,',
'    GROUP_ICON,',
'    GROUP_FOOTER,',
'    ID,',
'    TITLE,',
'    FOOTER',
'FROM',
'    G LEFT',
'    JOIN I',
'    USING ( GROUP_ID )',
'    RIGHT JOIN C',
'    USING ( COLUMN_ID )',
'ORDER BY',
'    COLUMN_ID,',
'    GROUP_ID'))
