,p_plug_display_sequence=>170
,p_include_in_reg_disp_sel_yn=>'Y'
,p_plug_display_point=>'BODY'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'WITH C AS (',
'    SELECT',
'        1 AS COLUMN_ID,',
'        ''To Do'' AS COLUMN_TITLE,',
'        ''fa-calendar'' AS COLUMN_ICON,',
'        ''hsl(190, 55%, 60%)'' AS COLUMN_COLOR,',
'        ''border-color:hsl(10, 70%, 50%);'' AS COLUMN_HEADER_STYLE',
'    FROM',
'        DUAL',
'    UNION ALL',
'    SELECT',
'        2 AS COLUMN_ID,',
'        ''In Progress'' AS COLUMN_TITLE,',
'        ''fa-wrench'' AS COLUMN_ICON,',
'        ''hsl(225, 55%, 60%)'' AS COLUMN_COLOR,',
'        ''border-color:hsl(45, 70%, 50%);'' AS COLUMN_HEADER_STYLE',
'    FROM',
'        DUAL',
'    UNION ALL',
'    SELECT',
'        3 AS COLUMN_ID,',
'        ''Done'' AS COLUMN_TITLE,',
'        ''fa-check'' AS COLUMN_ICON,',
'        ''hsl(260, 55%, 60%)'' AS COLUMN_COLOR,',
'        ''border-color:hsl(80, 70%, 50%);'' AS COLUMN_HEADER_STYLE',
'    FROM',
'        DUAL',
'), G AS (',
'    SELECT',
'        LEVEL   AS GROUP_ID,',
'        DBMS_RANDOM.STRING(''U'', 1)',
'        || DBMS_RANDOM.STRING(''L'', DBMS_RANDOM.VALUE(6, 12))',
'        || '' ''',
'        || DBMS_RANDOM.STRING(''L'', DBMS_RANDOM.VALUE(6, 12)) AS GROUP_TITLE,',
'        DBMS_RANDOM.STRING(''L'', DBMS_RANDOM.VALUE(8, 15))',
'        || '' ''',
'        || DBMS_RANDOM.STRING(''L'', DBMS_RANDOM.VALUE(8, 15))',
'        || '' ''',
'        || DBMS_RANDOM.STRING(''L'', DBMS_RANDOM.VALUE(8, 15))',
'        || '' ''',
'        || DBMS_RANDOM.STRING(''L'', DBMS_RANDOM.VALUE(8, 15)) AS GROUP_FOOTER,',
'        APEX_UTIL.PREPARE_URL(P_URL => ''f?p=''',
'                                       || :APP_ID',
'                                       || '':101:''',
'                                       || :APP_SESSION',
'                                       || ''::''',
'                                       || :DEBUG',
'                                       || '':::'') AS GROUP_LINK,',
'        APEX_UTIL.PREPARE_URL(P_URL => ''f?p=''',
'                                       || :APP_ID',
'                                       || '':101:''',
'                                       || :APP_SESSION',
'                                       || ''::''',
'                                       || :DEBUG',
'                                       || '':::'') AS GROUP_NEW_ITEM_LINK,',
'        CASE',
'            WHEN MOD(ROWNUM, 2) = 1 THEN',
'                ''fa-bug''',
'            ELSE ''fa-cogs''',
'        END GROUP_ICON,',
'        CASE',
'            WHEN MOD(ROWNUM, 2) = 1 THEN',
'                ''black;''',
'            ELSE NULL',
'        END GROUP_ICON_COLOR,',
'        CASE',
'            WHEN MOD(ROWNUM, 2) = 1 THEN',
'                ''background:hsl(0, 90%, 60%);''',
'            ELSE NULL',
'        END GROUP_HEADER_STYLE',
'    FROM',
'        DUAL',
'    CONNECT BY',
'        LEVEL <= 3',
'), I AS (',
'    SELECT',
'        1000 + LEVEL AS ID,',
'        TRUNC(DBMS_RANDOM.VALUE(1, 4)) AS COLUMN_ID,',
'        TRUNC(DBMS_RANDOM.VALUE(1, 4)) AS GROUP_ID,',
'        DBMS_RANDOM.STRING(''U'', 1)',
'        || DBMS_RANDOM.STRING(''L'', DBMS_RANDOM.VALUE(6, 12))',
'        || '' ''',
'        || DBMS_RANDOM.STRING(''L'', DBMS_RANDOM.VALUE(6, 12)) AS TITLE,',
'        ''<div class="chip">',
'  <img src="#APP_IMAGES#''',
'        || DECODE(TRUNC(DBMS_RANDOM.VALUE(0, 2)), 0, ''icon_female.png'', 1, ''icon_male.png'')',
'        || ''" width="40" height="50">''',
'        || DBMS_RANDOM.STRING(''U'', 1)',
'        || DBMS_RANDOM.STRING(''L'', DBMS_RANDOM.VALUE(6, 12))',
'        || '' ''',
'        || DBMS_RANDOM.STRING(''U'', 1)',
'        || DBMS_RANDOM.STRING(''L'', DBMS_RANDOM.VALUE(6, 12))',
'        || ''</div><table><colgroup><col span="1" style="width: 50%;"><col span="1" style="width: 50%;"></colgroup><tr><td>Text 1</td><td>''',
'        || APEX_ITEM.SWITCH(P_IDX => 1, P_VALUE => DECODE(MOD(ROWNUM, 2), 0, ''Y'', ''N''), P_ITEM_ID => ''IS_AWESOME_'' || ROWNUM, P_ITEM_LABEL => ''Is awesome'')',
'        || ''</td></tr><tr><td>Text 2</td><td>''',
'        || APEX_ITEM.SELECT_LIST_FROM_QUERY(P_IDX => 2, P_VALUE => TRUNC(DBMS_RANDOM.VALUE(1, 9)), P_QUERY => ''SELECT ROWNUM AS D FROM DUAL CONNECT BY LEVEL <= 8'', P_ITEM_ID => ''MY_SELECT_'' || ROWNUM, P_NULL_TEXT',
'        => '''', P_ATTRIBUTES => ''dir="rtl"'')',
'        || ''</td></tr></table>'' AS FOOTER,',
'        APEX_UTIL.PREPARE_URL(P_URL => ''f?p=''',
'                                       || :APP_ID',
'                                       || '':101:''',
'                                       || :APP_SESSION',
'                                       || ''::''',
'                                       || :DEBUG',
'                                       || '':::'') AS LINK,',
'        CASE',
'            WHEN MOD(ROWNUM, 5) = 2 THEN',
'                ''fa-rocket''',
'            ELSE NULL',
'        END ICON,',
'        CASE',
'            WHEN MOD(ROWNUM, 5) = 2 THEN',
'                ''black;''',
'            ELSE NULL',
'        END ICON_COLOR,',
'        CASE',
'            WHEN MOD(ROWNUM, 5) = 2 THEN',
'                ''background:#FFD700;''',
'            ELSE NULL',
'        END HEADER_STYLE',
'    FROM',
'        DUAL',
'    CONNECT BY',
'        LEVEL <= 10',
')',
'SELECT',
'    COLUMN_ID,',
'    COLUMN_TITLE,',
'    COLUMN_ICON,',
'    COLUMN_COLOR,',
'    COLUMN_HEADER_STYLE,',
'    GROUP_ID,',
'    GROUP_TITLE,',
'    GROUP_FOOTER,',
'    GROUP_ICON,',
'    GROUP_ICON_COLOR,',
'    GROUP_HEADER_STYLE,',
'    GROUP_LINK,',
'    GROUP_NEW_ITEM_LINK,',
'    ID,',
'    TITLE,',
'    FOOTER,',
'    ICON,',
'    ICON_COLOR,',
'    HEADER_STYLE,',
'    LINK',
'FROM',
'    G LEFT',
'    JOIN I',
'    USING ( GROUP_ID )',
'    FULL OUTER JOIN C',
'    USING ( COLUMN_ID )',
'ORDER BY',
'    COLUMN_ID,',
'    GROUP_ID'))
,p_plug_source_type=>'PLUGIN_MATERIAL.KANBAN'
,p_plug_query_options=>'DERIVED_REPORT_COLUMNS'
,p_attribute_01=>wwv_flow_string.join(wwv_flow_t_varchar2(
'{',
'    "dynamicColumns": true,',
'    "groupExtension": true,',
'    "groupColWidth": 6,',
'    "allowDragItemsBetweenGroups": true',
'}'))
,p_attribute_02=>'Y'
,p_attribute_03=>wwv_flow_string.join(wwv_flow_t_varchar2(
