,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'<dl>',
'  <dt><b>refresh</b> (number)</dt><dd>Refresh time of the kanban board in seconds. If 0 then no refresh will be set.</dd>',
'  <dt><b>staticColumns</b> (array)</dt><dd>defines columns and their attributes:',
'    <ul>',
'      <li><dt><b>COLUMN_ID</b></dt><dd>Identifier of the column. Items with the same value are assigned to this column</dd></li>',
'      <li><dt><b>COLUMN_TITLE</b></dt><dd>Titel of the column</dd></li>',
'      <li><dt><b>COLUMN_ICON</b></dt><dd>Default icon for all items in column</dd></li>',
'      <li><dt><b>COLUMN_COLOR</b></dt><dd>Default icon background color for all items in column</dd></li>',
'      <li><dt><b>COLUMN_HEADER_STYLE</b></dt><dd>Custom style attributes for column header</dd></li>',
'    </ul>',
'  </dd>',
'  <dt><b>dynamicColumns</b> (boolean)</dt><dd>If true then the columns are defined dynamically by SQL statement instead of the staticColumns attribute.</dd>',
'  <dt><b>groupExtension</b> (boolean)</dt><dd>If true then the group extension is activated and items must serve the column <b>GROUP_ID</b> in the SQL statement.</dd>',
'  <dt><b>groupColWidth</b> (number)</dt><dd>Relative size of the group column in relation to the other columns (4 = 33%, 6 = 50%, 12 = 100%).</dd>',
'  <dt><b>allowDragItemsBetweenGroups</b> (boolean)</dt><dd>Allow moving items across group boundaries.</dd>',
'  <dt><b>printDataToConsole</b> (boolean)</dt><dd>Debug feature: print loaded json data to console.</dd>',
'</dl>',
'<br>',
'<br>',
'<h4>Example:</h4>',
'<pre>',
'{',
'    "refresh": 0,',
'    "staticColumns": [{',
'        "COLUMN_ID": "1",',
'        "COLUMN_TITLE": "To Do",',
'        "COLUMN_ICON": "fa-calendar"',
'                        }, {',
'        "COLUMN_ID": "2",',
'        "COLUMN_TITLE": "In Progress",',
'        "COLUMN_ICON": "fa-wrench"',
'                        }, {',
'        "COLUMN_ID": "3",',
'        "COLUMN_TITLE": "Done",',
'        "COLUMN_ICON": "fa-check"',
'    }],',
'    "dynamicColumns": false,',
'    "groupExtension": false,',
'    "groupColWidth": 6,',
'    "allowDragItemsBetweenGroups": false,',
'    "printDataToConsole": false',
'}',
'</pre>'))
,p_plug_query_options=>'DERIVED_REPORT_COLUMNS'
,p_attribute_01=>'N'
,p_attribute_02=>'HTML'
);
